import android.app.Application
import androidx.lifecycle.MediatorLiveData
import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.ViewModel
import com.xiqu.android.base.bean.errorMessage
import com.xiqu.android.base.bean.isSuccess
import com.xiqu.android.common.ktx.asLiveData
import com.xiqu.android.common.ktx.toast
import javax.inject.Inject

/**
 * @author mmw
 * @date $DATE$
 **/
class $ViewModel$ @Inject constructor(
        private val application: Application,
        private val $repository$: $Repository$
) : ViewModel() {

    val loading = MediatorLiveData<Boolean>()

    val loadMore = MutableLiveData<Boolean>()

    val $results$ = MutableLiveData<$Result$>()

    private var currentPage = 1

    fun onRefresh($param$: $ParamType$) {
        $getList$($param$)
    }

    fun $getList$($param$: $ParamType$, isLoadMore: Boolean = false) {
        loadMore.value = isLoadMore
        if (!isLoadMore) {
            loading.value = true
        }
        val page = if (isLoadMore) currentPage else 1
        loading.addSource($repository$.$getListFlowable$.asLiveData()) {
            loading.value = false
            it.isSuccess()?.apply {
                currentPage = page + 1
                $results$.value = this
            } ?: apply {
                application.toast(it.errorMessage())
                $results$.value = null
            }
        }
    }

}

import android.os.Bundle
import androidx.lifecycle.Observer
import androidx.recyclerview.widget.LinearLayoutManager
import com.xiqu.android.base.delegate.autoCleared
import $modulePackage$.R

/**
 * @author mmw
 * @date $date$
 **/
class $Name$$Controller$ : Base$Controller$<$Binding$, $ViewModel$>() {

    private var mAdapter by autoCleared<$Name$Adapter>()

    override fun getLayoutId(): Int = R.layout.$layoutId$

    override fun setupDaggerComponent() {
        super.setupDaggerComponent()
        $ConnectModule$.requireModuleComponent().inject(this)
    }

    override fun init(savedInstanceState: Bundle?) {
        mBinding.recyclerView.apply {
            layoutManager = LinearLayoutManager(mContext)
        }
        mAdapter = $Name$Adapter().apply {
            bindToRecyclerView(mBinding.recyclerView)
            setOnItemClickListener { _, _, position ->
                mAdapter.getItem(position)?.apply {

                }
            }
        }

        mViewModel.$results$.observe(this, Observer {
            val loadMore = mViewModel.loadMore.value ?: false
            it?.apply {
                if (!loadMore) {
                    mAdapter.setNewData(it.list)
                    if (!this.isHasNext) {
                        mAdapter.loadMoreEnd()
                    }
                } else {
                    mAdapter.addData(it.list)
                    if (this.isHasNext) {
                        mAdapter.loadMoreComplete()
                    } else {
                        mAdapter.loadMoreEnd()
                    }
                }
            } ?: apply {
                if (loadMore) {
                    mAdapter.loadMoreFail()
                }
            }
        })

        mBinding.viewModel = mViewModel
        mBinding.setLifecycleOwner(this)
        mBinding.executePendingBindings()

        mViewModel.onRefresh()
    }

}

@Inject constructor(
        private val application: Application
) : ViewModel() {

}

mViewModel.loadingDialog.observe(this, Observer {
    if (it == true) {
        loadingDialog.show()
    } else {
        loadingDialog.dismiss()
    }
})

